import React, { useState } from 'react';

function App() {
  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [operator, setOperator] = useState('');
  const [result, setResult] = useState('');

  const calculate = async () => {
    const response = await fetch('http://localhost:5000', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ num1: parseInt(num1), num2: parseInt(num2), operator }),
    });
    const data = await response.json();
    setResult(data.result);
  };

  return (
    <div style={{ textAlign: 'center', margin: '50px auto', maxWidth: '400px' }}>
      <label htmlFor="num1">Enter a number:</label>
      <input
        type="number"
        value={num1}
        onChange={(e) => setNum1(e.target.value)}
        style={{ marginBottom: '10px', width: '100%', padding: '5px' }}
      />
     <label htmlFor="operator">Enter an operation:</label>
     <input
        type="text"
        value={operator}
        onChange={(e) => setOperator(e.target.value)}
        style={{ marginBottom: '10px', width: '100%', padding: '5px' }}
      />
      <label htmlFor="num2">Enter a number:</label>
      <input
        type="number"
        value={num2}
        onChange={(e) => setNum2(e.target.value)}
        style={{ marginBottom: '10px', width: '100%', padding: '5px' }}
      />
      <button onClick={calculate} style={{ width: '100%', padding: '10px', background: 'blue', color: 'white', border: 'none' }}>
        Calculate
      </button>
      <div style={{ marginTop: '20px', fontSize: '18px' }}>Result: {result}</div>
    </div>
  );
}

export default App;
